toolScript.printScript:
function makeCalib(x){
  y={pathSpeed: x.pathSpeed,
	 pathHeight: x.pathHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.areaConstant,
	 compressionVolume: x.compressionVolume
	}
  return y;
}

progress.setSteps(blueTaperdefault.meshes.length*2 + 3);

slicer.setSliceHeight(blueTaperdefault.pathHeight);
pather.set("PathWidth", blueTaperdefault.pathWidth);
for (var i = 0; i < blueTaperdefault.meshes.length; ++i) {
  progress.log("Slicing blueTaperdefault Mesh");
  slicer.doSlicing(blueTaperdefault.meshes[i]);
  progress.step();
  progress.log("Pathing blueTaperdefault Mesh");
  pather.doPathing(blueTaperdefault.meshes[i]);
  progress.step();
}

var blueTaperdefaultMaterialCalibration = makeCalib(blueTaperdefault);
var fabWriter = fabFile.fabAtHomeModel2Writer();
fabWriter.addMeshes("default", blueTaperdefaultMaterialCalibration, blueTaperdefault.meshes);
progress.step();
fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);
progress.step();
fabWriter.print();
progress.finish();
